selector_to_html = {"a[href=\"#diversity-inclusion\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Diversity &amp; Inclusion</strong><a class=\"headerlink\" href=\"#diversity-inclusion\" title=\"Link to this heading\">#</a></h3><p>We are committed to fostering an <strong>inclusive learning environment</strong> and welcome students from all backgrounds.<br/>\nFor concerns related to <strong>discrimination, harassment, or bias</strong>, please reach out to <a class=\"reference external\" href=\"https://www.buffalo.edu/diversity.html\">UB\u2019s Diversity Office</a>.</p>", "a[href=\"#course-information\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Course Information<a class=\"headerlink\" href=\"#course-information\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#late-submission-policy\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Late Submission Policy</strong><a class=\"headerlink\" href=\"#late-submission-policy\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#collaboration-academic-integrity\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Collaboration &amp; Academic Integrity</strong><a class=\"headerlink\" href=\"#collaboration-academic-integrity\" title=\"Link to this heading\">#</a></h3><p>More details on UB\u2019s <strong>Academic Integrity Policy</strong>:<br/>\n<a class=\"reference external\" href=\"https://www.buffalo.edu/studentlife/who-we-are/departments/accessibility.html\">https://www.buffalo.edu/studentlife/who-we-are/departments/accessibility.html</a></p>", "a[href=\"#summary-of-the-syllabus\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Summary of the syllabus<a class=\"headerlink\" href=\"#summary-of-the-syllabus\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#regrading-policy\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Regrading Policy</strong><a class=\"headerlink\" href=\"#regrading-policy\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#course-logistics-and-syllabus\"]": "<h1 class=\"tippy-header\" style=\"margin-top: 0;\">Course Logistics and Syllabus<a class=\"headerlink\" href=\"#course-logistics-and-syllabus\" title=\"Link to this heading\">#</a></h1><h2>Summary of the syllabus<a class=\"headerlink\" href=\"#summary-of-the-syllabus\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#course-learning-outcomes\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Course Learning Outcomes<a class=\"headerlink\" href=\"#course-learning-outcomes\" title=\"Link to this heading\">#</a></h2><p>Upon successful completion of this course, students will be able to:</p>", "a[href=\"#course-materials\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Course Materials<a class=\"headerlink\" href=\"#course-materials\" title=\"Link to this heading\">#</a></h2><h3><strong>Recommended Textbooks</strong><a class=\"headerlink\" href=\"#recommended-textbooks\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#course-description\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Course Description<a class=\"headerlink\" href=\"#course-description\" title=\"Link to this heading\">#</a></h2><p>This course provides an introduction to the fundamental techniques in <strong>Computer Vision and Image Processing (CVIP)</strong> with an emphasis on physical, mathematical, and information-processing aspects of vision.</p><p>Topics covered include:</p>", "a[href=\"#course-schedule\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Course Schedule<a class=\"headerlink\" href=\"#course-schedule\" title=\"Link to this heading\">#</a></h2><p><em>(Note: Schedule is subject to changes. Minor changes to content)</em></p>", "a[href=\"#accessibility-accommodations\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Accessibility &amp; Accommodations</strong><a class=\"headerlink\" href=\"#accessibility-accommodations\" title=\"Link to this heading\">#</a></h3><p>Students requiring accommodations should contact the <strong>Office of Accessibility Resources</strong> in <strong>60 Capen Hall</strong>.<br/>\nPhone: <strong>(716) 645-2608</strong> | Website: <a class=\"reference external\" href=\"http://www.buffalo.edu/studentlife/who-we-are/departments/accessibility.html\">Accessibility Office</a></p>", "a[href=\"#mental-health-well-being\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Mental Health &amp; Well-being</strong><a class=\"headerlink\" href=\"#mental-health-well-being\" title=\"Link to this heading\">#</a></h3><p>UB provides counseling and mental health services for students dealing with stress, anxiety, or other challenges.<br/>\n<strong>Counseling Services:</strong></p>", "a[href=\"#grading-policy\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Grading Policy<a class=\"headerlink\" href=\"#grading-policy\" title=\"Link to this heading\">#</a></h2><p><strong>Grading Scale:</strong></p>", "a[href=\"#final-notes\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Final Notes<a class=\"headerlink\" href=\"#final-notes\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#student-resources\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Student Resources<a class=\"headerlink\" href=\"#student-resources\" title=\"Link to this heading\">#</a></h2><h3><strong>Accessibility &amp; Accommodations</strong><a class=\"headerlink\" href=\"#accessibility-accommodations\" title=\"Link to this heading\">#</a></h3><p>Students requiring accommodations should contact the <strong>Office of Accessibility Resources</strong> in <strong>60 Capen Hall</strong>.<br/>\nPhone: <strong>(716) 645-2608</strong> | Website: <a class=\"reference external\" href=\"http://www.buffalo.edu/studentlife/who-we-are/departments/accessibility.html\">Accessibility Office</a></p>", "a[href=\"#prerequisites\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Prerequisites<a class=\"headerlink\" href=\"#prerequisites\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#recommended-textbooks\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Recommended Textbooks</strong><a class=\"headerlink\" href=\"#recommended-textbooks\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#attendance-policy\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Attendance Policy</strong><a class=\"headerlink\" href=\"#attendance-policy\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#software-and-tools\"]": "<h3 class=\"tippy-header\" style=\"margin-top: 0;\"><strong>Software and Tools</strong><a class=\"headerlink\" href=\"#software-and-tools\" title=\"Link to this heading\">#</a></h3>", "a[href=\"#instructor-information\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Instructor Information<a class=\"headerlink\" href=\"#instructor-information\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#contact-communication\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Contact &amp; Communication<a class=\"headerlink\" href=\"#contact-communication\" title=\"Link to this heading\">#</a></h2>", "a[href=\"#academic-policies\"]": "<h2 class=\"tippy-header\" style=\"margin-top: 0;\">Academic Policies<a class=\"headerlink\" href=\"#academic-policies\" title=\"Link to this heading\">#</a></h2><h3><strong>Attendance Policy</strong><a class=\"headerlink\" href=\"#attendance-policy\" title=\"Link to this heading\">#</a></h3>"}
skip_classes = ["headerlink", "sd-stretched-link"]

window.onload = function () {
    for (const [select, tip_html] of Object.entries(selector_to_html)) {
        const links = document.querySelectorAll(`main ${select}`);
        for (const link of links) {
            if (skip_classes.some(c => link.classList.contains(c))) {
                continue;
            }

            tippy(link, {
                content: tip_html,
                allowHTML: true,
                arrow: false,
                placement: 'auto-start', maxWidth: 500, interactive: true, boundary: document.body, appendTo: document.body,
                onShow(instance) {MathJax.typesetPromise([instance.popper]).then(() => {});},
            });
        };
    };
    console.log("tippy tips loaded!");
};
